import React, { useEffect, useState } from 'react'
import { AgeCartComponent } from './AgeCartComponent';
interface ChildrenAgesComponentProps {
    childrenCount: number;
    onHandleAgeChange: (index: number, value: number) => void;
}
export const ChildrenAgesComponent: React.FC<ChildrenAgesComponentProps> = ({ childrenCount, onHandleAgeChange }) => {
    const [ages, setAges] = useState<number[]>(Array<number>(childrenCount));
    useEffect(() => {
        setAges(Array<number>(childrenCount))
    }, [childrenCount])
    return (
        <section className='ChildrenAgesComponent'>
            {ages.map((value, index) => (
               <input 
                    type="number" 
                    value={value}
                    onChange={(e)=>onHandleAgeChange(index, Number(e.target.value))}
                />
            ))}
        </section>
    )
}
