import React, { useEffect, useState } from 'react';
import { room } from '../interfaces/Room';
import { AgesForm } from './AgesForm';

type Field = 'adultCount' | 'childCount';

interface GuestFormProps {
    onSendRoomData: (rooms: Map<number, room>) => void;
}

export const GuestForm: React.FC<GuestFormProps> = ({ onSendRoomData }) => {
    const initialRooms = new Map<number, room>();
    initialRooms.set(1, { adultCount: 1, child: { childCount: 0, ages: [] } });
    const [rooms, setRooms] = useState<Map<number, room>>(initialRooms);
    const [nexRoomId, setNextRoomId] = useState<number>(2);

    const handleAddRoom = () => {
        const newRoom = {
            adultCount: 1,

            child: {
                childCount: 0,
                ages: []
            }
        };
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            newRooms.set(nexRoomId , newRoom);
            return newRooms
        });
        setNextRoomId(prevId => prevId + 1);
    };

    useEffect(() => {
        onSendRoomData(rooms);
    }, [rooms, onSendRoomData]);

    console.log(rooms, 'From Rooms');

    const handleAgesChange = (id: number, newAges: number[]) => {
        setRooms((prevRooms) => {
            const newRooms = new Map(prevRooms);
            const room = newRooms.get(id);
            if (room) {
                newRooms.set(id, {
                    ...room,
                    child: {
                        ...room.child,
                        ages: newAges
                    }
                });
            }
            return newRooms;
        });
    };
    const handleDeleteRoom = (id: number) => {
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            newRooms.delete(id);
            return newRooms
        });
        setNextRoomId(prevId => prevId - 1);
    };

    const handleRoomGuestChange = (id: number, field: Field, value: number) => {
        setRooms(prevRooms => {
            const newRooms = new Map(prevRooms);
            const room = newRooms.get(id)
            if (room) {
                if (field === "adultCount") {
                    newRooms.set(id, {
                        ...room,
                        adultCount: value
                    })
                } else if (field === "childCount") {
                    newRooms.set(id, {
                        ...room,
                        child: {
                            ...room.child,
                            childCount: value
                        }
                    })
                }

            }
            return newRooms;
        });
    };

    return (
        <div className='GuestForm'>
            {Array.from(rooms.entries()).map(([index, room]) => (

                <form key={index}>
                    <input
                        type="number"
                        value={room.adultCount}
                        onChange={(e) => handleRoomGuestChange(index, 'adultCount', Number(e.target.value))}
                    />
                    <input
                        type="number"
                        value={room.child.childCount}
                        onChange={(e) => handleRoomGuestChange(index, 'childCount', Number(e.target.value))}
                    />
                    {room.child.childCount > 0 && (
                        <AgesForm
                            childCount={room.child.childCount}
                            onChildrenAges={(newChildAges) => handleAgesChange(index, newChildAges)}
                        />
                    )}
                    {index > 1 && (
                        <button type='button' onClick={() => handleDeleteRoom(index)}>Delete</button>
                    )}
                </form>
            ))}
            <button onClick={handleAddRoom}>addRoom</button>
        </div>
    );
};
