import React from 'react'
import { Room } from '../interfaces/Room'
interface SelectAgesProps {
    room: Room
    onAgeInputChange: (index: number, value: number) => void;

}
export const SelectAges: React.FC<SelectAgesProps> = ({ room, onAgeInputChange}) => {
    //const [ages, setAges] = useState<number[]>(Array.from({ length: room.child.childCount }));

    return (
        <div className='SelectAges'>
            {room.child.childCount > 0 && (
                Array.from({ length: room.child.childCount }).map((_, index) => (
                    <select name="" id="" key={index}
                    value={room.child.ages[index]}
                        onChange={(e) => onAgeInputChange(index, Number(e.target.value))}
                    >
                        <option value=''> Select child {index + 1} Age</option>
                         {[...Array(18)].map((_, index)=>(
                            <option key={index} >{index}</option>
                         ))} 
                    </select>
                ))
            )}
        </div>
    )
}
