import React, { useEffect, useState } from 'react';

interface AgeCartComponentProps {
    childrenCount: number;
}

export const AgeCartComponent: React.FC<AgeCartComponentProps> = ({ childrenCount }) => {
    const [ages, setAges] = useState<string[]>(Array(childrenCount).fill("no selected"));

    useEffect(() => {
        setAges(Array(childrenCount).fill('no selected'));
    }, [childrenCount]);

    const handleAgeChange = (index: number, value: string) => {
        const newAges = [...ages];
        newAges[index] = value;
        setAges(newAges);
    };

    return (
        <div className='AgeCartComponent'>
            {ages.map((age, index) => (
                <select 
                    key={index}
                    value={age}
                    onChange={(e) => handleAgeChange(index, e.target.value)}
                >
                    <option value="no selected">Select child age</option>
                    {[...Array(18)].map((_, optionIndex) => (
                        <option key={optionIndex} value={optionIndex.toString()}>
                            {optionIndex}
                        </option>
                    ))}
                </select>
            ))}
        </div>
    );
};
