import React, { useEffect, useState } from 'react';
import { useBookingContext } from './ContexApplication';
interface AgeCartComponentProps {
    childrenCount: number;
    onAgeChange:(childAgeIndex:number, value:string)=>void;
}

export const AgeCartComponent: React.FC<AgeCartComponentProps> = ({ childrenCount, onAgeChange }) => {
    const [localAges, setLocalAges] = useState<string[]>(Array(childrenCount).fill("no selected"));
    const {setBookingData} = useBookingContext();


    useEffect(() => {
        if(childrenCount > localAges.length){
            const newAges = [...localAges, ...Array(childrenCount - localAges.length).fill("no age selected")];
            setLocalAges(newAges)
        }else if(childrenCount < localAges.length){
            const newAges = localAges.slice(0, childrenCount);
            setLocalAges(newAges); 
        }
    }, [childrenCount]);


    const handleAgeChange = (index: number, value: string) => {
        const newAges = [...localAges];
        newAges[index] = value;
        setLocalAges(newAges);
        onAgeChange(index, value);
    };
    console.log(localAges);
    return (
        <div className='AgeCartComponent'>
            {localAges.map((age, index) => (
                <select 
                    key={index}
                    value={age}
                    onChange={(e) => handleAgeChange(index, e.target.value)}
                >
                    <option value="no selected">Select child {index + 1} Age</option>
                    {[...Array(18)].map((value, optionIndex) => (
                        <option key={optionIndex} value={value}>
                           {optionIndex} years old
                        </option>
                    ))}
                </select>
            ))}
        </div>
    );
};
