import React, { createContext, useState, useContext, useMemo } from "react";
import { resevationData } from "../interfaces/ReservationData";
import { Room } from "../interfaces/Room";
import { date } from "../interfaces/BookedDate";
type field = "adultCount" | "childCount";
interface BookingContextProps {
    bookingData: resevationData;
    setBookingData: React.Dispatch<React.SetStateAction<resevationData>>;
    handleAddNewRoom: () => void;
    handleDeleteRoom: (index: number) => void;
    handleGuestCountChange: (field: field, index: number, value: number) => void;
    handleAgesChange: (roomIndex: number, newAges: string[]) => void;
    updateAgesLenght: (index: number, newLength: number) => void;
    handleAgeByIndex: (roomIndex: number, value: string[]) => Map<number, Room>;
    getAges: (currentAges: string[], newChildrenCount: number) => string[];
    handleDateUpdate: (field: keyof date, e: React.ChangeEvent<HTMLInputElement>) => void;
    handleNextClick: () => void;
    StatGuestFormatted: string;
};

interface bookingProviderProps {
    children: React.ReactNode;
}
// const CheckFrecuency = (numbers:number[]):Map<number, number>=>{
//     return numbers.reduce((freq, value)=>{
//         freq.set(value, (freq.get(value)|| 0 ) + 1);
//         return freq;
//     }, new Map<number,number>);  
// }
const BookingContext = createContext<BookingContextProps | undefined>(undefined);
//Proveedor del contexto 
export const BookingProvider: React.FC<bookingProviderProps> = ({ children }) => {
    const inicialRoom = new Map<number, Room>();
    inicialRoom.set(1, { adultCount: 1, child: { childCount: 0, ages: [] } });
    const [bookingData, setBookingData] = useState<resevationData>({
        date: { checkIn: new Date(), checkOut: new Date() },
        rooms: inicialRoom,
        guest: { name: '', lastName: '', email: '', phone: '' }
    });
    const handleAddNewRoom = () => {
        //creamos un objeto con el identificador newRom de interface room, le asignamos sus correspondientes valores
        const newRoom: Room = {
            adultCount: 1,
            child: { childCount: 0, ages: [] }
        }
        //Hacemos una copia del estado anterior de rooms
        const updateRooms = new Map(bookingData.rooms);
        // Incertamos new room que acabamos de crear, con el parametro number igual al 
        // estado rooms anterior le asignamos el valor rooms.size()
        updateRooms.set(bookingData.rooms.size + 1, newRoom);
        //Llamamos la funcion actuaciona, hacemos una copia del estado anterior, y modificamos el estado anterior de rooms
        //con los cambios anteriores realizados.
        setBookingData(prev => ({
            ...prev,
            rooms: updateRooms
        }))
    }
    const getAges = (currentAges: string[], newChildrenCount: number): string[] => {
        if (newChildrenCount > currentAges.length) {
            return [...currentAges, ...Array(newChildrenCount - currentAges.length).fill("")];
        } else if (newChildrenCount < currentAges.length) {
            return currentAges.slice(0, newChildrenCount);
        }
        return currentAges;
    }
    const handleDeleteRoom = (index: number) => {
        setBookingData(prev => {
            const updateRooms = new Map(prev.rooms);
            updateRooms.delete(index); // room siempre es true ya que el botton delete siempre se mostrara cu
            return {
                ...prev,
                rooms: updateRooms
            }
        })
    }
    const handleDateUpdate = (field: keyof date, e: React.ChangeEvent<HTMLInputElement>) => {
        const newDate = new Date(e.target.value);
        setBookingData(prev => ({
            ...prev,
            date: {
                ...prev.date,
                [field]: newDate,
            },
        }));
    };
    const handleGuestCountChange = (field: field, index: number, value: number) => {
        setBookingData(prev => {
            const updateRooms = new Map(prev.rooms);
            const GuestUpdate = updateRooms.get(index);
            if (GuestUpdate) {
                if (field === 'adultCount') {
                    GuestUpdate.adultCount = value;
                } else if (field === 'childCount') {
                    GuestUpdate.child.childCount = value
                }
                updateRooms.set(index, GuestUpdate);
                return {
                    ...prev,
                    rooms: updateRooms
                }
            }
            return prev;

        });
    }
    const checkAges = (): boolean => {
        for (const room of Array.from(bookingData.rooms.values())) {
            let index = 0;
            for (const value of room.child.ages) {
                if (value === "" || value === `--Select child ${index + 1} Age--`) {
                    return false;
                }
                index++;
            }
        }
        return true;
    };
    const handleNextClick = () => {
        const isValid = checkAges();
        if (!isValid) {
            alert("Please ensure all child ages are selected.");
        }
    }
    const handleAgeByIndex = (roomIndex: number, value: string[]): Map<number, Room> => {
        const roomsUpdate: Map<number, Room> = new Map(bookingData.rooms);
        const roomToUpdate = roomsUpdate.get(roomIndex);
        if (roomToUpdate) {
            roomToUpdate.child.ages = value;
        }
        return roomsUpdate;
    };
    const updateAgesLenght = (index: number, newLength: number) => {
        const updateRooms = new Map(bookingData.rooms);
        const room = updateRooms.get(index);
        if (room) {
            room.child.ages = Array(newLength).fill(" ");
            updateRooms.set(index, room);
            setBookingData({ ...bookingData, rooms: updateRooms });
        }
    }
    const handleAgesChange = (roomIndex: number, newAges: string[]) => {
        const updateRooms = new Map(bookingData.rooms);
        const room = updateRooms.get(roomIndex);
        if (room) {
            room.child.ages = newAges;
            updateRooms.set(roomIndex, room);
            setBookingData({ ...bookingData, rooms: updateRooms })
        }
    }
    const getGuestInfo = (input: string): string | number => {
        if (input !== 'stat' && input !== 'sum') {
            throw new Error("Input must be 'stat' or 'sum'");
        }
        const calculateSum = (key: field): number => {
            return Array.from(bookingData.rooms.values()).reduce((sum, room) => {
                if (key === 'adultCount') {
                    return sum + room.adultCount;
                } else if (key === 'childCount') {
                    return sum + room.child.childCount;
                }
                return sum;
            }, 0);
        };
        const adultSum = calculateSum('adultCount');
        const childSum = calculateSum('childCount');
        const roomsSum = bookingData.rooms.size;
        if (input === 'stat') {
            const adultTitle = adultSum > 1 ? 'adults' : 'adult';
            const childTitle = childSum > 1 ? 'children' : 'child';
            const roomTitle = roomsSum > 1 ? 'rooms' : 'room';
            const StatGuestFormatted = childSum === 0 ?
                `${adultTitle} ${adultSum}, ${roomTitle}  ${roomsSum}` :
                `${adultTitle} ${adultSum}, ${childTitle} ${childSum}, ${roomTitle} ${roomsSum}`;
            return StatGuestFormatted;
        } else if (input === 'sum') {
            return (adultSum + childSum);
        }
        return 0
    }


    return (
        <BookingContext.Provider
            value={{
                bookingData, setBookingData, handleAddNewRoom, handleDeleteRoom, handleGuestCountChange,
                handleAgesChange, updateAgesLenght, handleAgeByIndex, handleDateUpdate, handleNextClick, getAges
                , StatGuestFormatted
            }}
        >
            {children}
        </BookingContext.Provider>
    );
};

export const useBookingContext = () => {
    const context = useContext(BookingContext);
    if (context === undefined) {
        throw new Error('useBookingContext must be used within a BookingProvider');
    }
    return context;
}
