import React, { useEffect, useState } from 'react'

interface AgesFormProps {
    childCount: number;
}
export const AgesForm: React.FC<AgesFormProps> = ({ childCount }) => {
    const [ages, setAges] = useState<number[]>(Array(childCount).fill(0));
    useEffect(() => {
        setAges(Array(childCount).fill(0))
    }, [childCount])
    const AgesChange = (ageKey: number, newAge: number) => {
        setAges(prevAges => {
            const newAges = [...prevAges]
            newAges[ageKey] = newAge
            return newAges
        })
    }

    // const AgesChange = (ageKey: number, newAge: number) => {
    //     setAges(prevAges =>
    //         prevAges.map((age, index) => index === ageKey ? newAge : age)
    //     )
    // }

    return (
        <div className='AgesForm'>
            {ages.map((age, index) => (
                <input
                    key={index}
                    type="number"
                    value={age}
                    onChange={(e)=>AgesChange(index, Number(e.target.value))}
                />
            ))
            }
        </div>
    )
}
