import React,{useMemo} from 'react'
import { useBookingContext } from './ContexApplication';
interface ShowRoomInfoComponentProps{
    onClickedInput: (input: boolean) => void;
}
export const ShowRoomInfoComponent:React.FC<ShowRoomInfoComponentProps> = ({onClickedInput}) => {
    const{bookingData} = useBookingContext();
    const adultSum = useMemo<number>(() => Array.from(bookingData.rooms.values()).reduce((sum, room) => sum + room.adultCount, 0), [bookingData.rooms]);
    const childSum = useMemo<number>(() => Array.from(bookingData.rooms.values()).reduce((sum, adult) => sum + adult.child.childCount, 0), [bookingData.rooms]);
    const roomsSum = useMemo<number>(() => bookingData.rooms.size, [bookingData.rooms]);

    const adultTitle = adultSum > 1 ? 'adults' : 'adult';
    const childTitle = childSum > 1 ? 'children' : 'child';
    const roomTitle = roomsSum > 1 ? 'rooms' : 'room';
    const formattedStat = childSum === 0 ?
        `${adultTitle} ${adultSum}, ${roomTitle}  ${roomsSum}` :
        `${adultTitle} ${adultSum}, ${childTitle} ${childSum} ${roomTitle} ${roomsSum}`;
  return (
    <section className='ShowRoomInfoComponent'>
     <input 
        type="text"
        readOnly
        value={formattedStat}
        onClick={()=>onClickedInput(true)}
    />
    </section>
  )
}
