import React from 'react';
import { Room } from '../interfaces/Room';
import "../css/guestForm.css";
import { SelectAges } from './SelectAges';
import { GuestInput } from './GuestInput';

type field = "adultCount" | "childCount";
interface GuestFormProps {
    rooms: Map<number, Room>;
    onHandleGuestCountChange: (index: number, field: field, value: number) => void;
    onDeleteRoom: (index: number) => void;
    onAddRoom: () => void;
    onChildAgeChange: (roomId: number, childId: number, value: number) => void;
    onSetClikedInput: (input: boolean) => void;
}

export const GuestForm: React.FC<GuestFormProps> = (
    {
        rooms,
        onHandleGuestCountChange,
        onDeleteRoom,
        onAddRoom,
        onSetClikedInput,
        onChildAgeChange
    }) => {

    return (
        <div className='GuestForm '
        >
            {Array.from(rooms.entries()).map(([roomIndex, room]) => (
                <form key={roomIndex} >

                    <GuestInput
                        room={room}
                        onHandleGuestChange={(field, value) => onHandleGuestCountChange(roomIndex, field, value)}
                    />
                    <SelectAges
                        room={room}
                        onAgeInputChange={(agesIndex, value) => onChildAgeChange(roomIndex, agesIndex, value)}
                    />
                    {roomIndex > 1 && <button className='DeleteButton' onClick={() => onDeleteRoom(roomIndex)}>Delete</button>}

                </form>

            ))}
            <button
                type='button' onClick={onAddRoom}
                style={{ padding: "0.5em", border: 'none' }}
            >Add Room
            </button>
            <button
                type='button'
                onClick={(inputClicked) => onSetClikedInput(!inputClicked)}
                style={{ padding: "0.5em", border: "none" }}
            >
                Next
            </button>

        </div>
    );
};
