import React, { useEffect, useState } from 'react'
import { Data } from '../interfaces/Data'
import "../css/selectedRooms.css";
interface SelectedRoomsProps {
    data: Data[];
    onSelectedRoomsChange: (selectedRooms: Data[]) => void;
    totalGuest: number;
    onNext: () => void;
    onPrevious: () => void;
}
export const SelectedRooms: React.FC<SelectedRoomsProps> = ({ data, onSelectedRoomsChange, totalGuest, onNext, onPrevious }) => {
    const availableRooms = data.filter(room => !room.booked);

    const [selectedRooms, setSelectedRooms] = useState<Data[]>([]);

    const handleRoomSelection = (room: Data) => {
        const isSelected = selectedRooms.some((selectedRoom) => selectedRoom.id === room.id);
        if (isSelected) {
            const updateRooms = selectedRooms.filter((selectedRoom) => selectedRoom.id !== room.id);
            setSelectedRooms(updateRooms);
        } else {
            const totalGuestSelected = selectedRooms.reduce((total, selectedRoom) => total + selectedRoom.maxGuest, 0);
            const newtotalGuest = totalGuestSelected + room.maxGuest;
            if (newtotalGuest <= totalGuest) {
                setSelectedRooms([...selectedRooms, room]);
            } else {
                alert('Cannot select more rooms than the total number of guests.');
            }
        }

    }
    const handleNext = () => {
        const totalSelectedGuests = selectedRooms.reduce((total, selectedRoom) => total + selectedRoom.maxGuest, 0);
        if (totalSelectedGuests >= totalGuest) {
            onNext();
        } else {
            alert('You must select enough rooms to accommodate all guests.');
        }
    }
    useEffect(() => {
        onSelectedRoomsChange(selectedRooms)
    }, [selectedRooms, onSelectedRoomsChange])

    return (

        <div className="SelectedRoomsConteiner">
            <div className='SelectedRooms flex'>
                <button onClick={() => onPrevious()}>Prevuis Page</button>
                <h1 style={{ textAlign: "center" }}>Select Rooms</h1>
                {availableRooms.map((room) => (
                    <div key={room.id} className='SelectedRoomsItem'>
                        <div className='SelectedImage' >
                            <img src={room.src} alt={room.category}
                                style={{ minWidth: "40rem", height: "20rem", objectFit: "cover" }}
                            />
                        </div>
                        <div className='SelectedRoomsDescription'
                            style={{ width: "30rem" }}>
                            <h2>{room.category}</h2>
                            <p>Description: {room.description}</p>
                            <p>Price: {room.price}$</p>
                            <p>Max Guests: {room.maxGuest}</p>
                            <button onClick={() => handleRoomSelection(room)}
                                style={{
                                    marginTop: "1em", padding: "1em", border: "none",
                                    minWidth: "10em"
                                }}
                                className='RoomSelectorBotton'>
                                {selectedRooms.some((selectedRoom) => selectedRoom.id === room.id) ? 'Deselect' : 'Select'}
                            </button>
                        </div>

                    </div>
                ))}
            </div>
            <div style={{ display: 'flex', justifyContent: 'center' }} className='BottonNext'>
                <button onClick={handleNext}
                    style={{
                        marginTop: "1em", padding: "1em", border: "none",
                        minWidth: "10em"
                    }}
                >Next
                </button>
            </div>
        </div>

    )
}
