import React from 'react'
import { useBookingContext } from './ContexApplication';
//import { Room } from '../interfaces/Room';
import { GuestCart } from './GuestCart';
import { ChildrenAgesComponent } from './ChildrenAgesComponent';
//import { ChildrenAgesComponent } from './ChildrenAgesComponent';
export const RoomManagemantComponent: React.FC = () => {
    const {
        bookingData,
        handleAddNewRoom,
        handleDeleteRoom,
        handleGuestCountChange,
        handleAgesChange
    } = useBookingContext();
    console.log(bookingData);
    return (
        <section
            className='RoomManagemantComponent'
        >
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room]) => (
                <section key={roomIndex}>
                    <GuestCart
                        onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                        room={room}
                        key={roomIndex}
                    />
                    {roomIndex > 1 && <button onClick={() => handleDeleteRoom(roomIndex)}>delete</button>}
                    <ChildrenAgesComponent/>
                </section>

            ))}
            <button onClick={handleAddNewRoom}>Add New Room</button>
        </section>
    )
}
