import React, { useState } from 'react'
import { BookedDate } from '../interfaces/BookedDate'
import { DateForm } from './DateForm';
import { ResevationData } from '../interfaces/ReservationData';
import { StatBooking } from './StatBooking';
import { GuestForm } from './GuestForm';
import { Room } from '../interfaces/Room';

type field = 'adultCount' | 'childCount';
export const BookingForm: React.FC = () => {
    const dateToday = new Date().toISOString().split('T')[0];
    const [date, setDate] = useState<BookedDate>({ checkIn: dateToday, checkOut: dateToday });
    const [inputClicked, setInputClicked] = useState<boolean>(false);
    const [reservationData, setReservationData] = useState<ResevationData>({
        date: date,
        rooms: [
            {
                adultCount: 3,
                child: { childCount: 0, ages: [] }
            }
        ]
    });
    const handleInputClicked = () => {
        setInputClicked(true);
    }
    const handleGuestChange = (roomIndex: number, field: field, value: number) => {
        setReservationData(prev => {
            const newRooms = [...prev.rooms];
            if (field === 'adultCount') {
                newRooms[roomIndex] = {
                    ...newRooms[roomIndex],
                    [field]: value
                };
            } else if (field === 'childCount') {
                newRooms[roomIndex] = {
                    ...newRooms[roomIndex],
                    child: { childCount: value, ages: [] }
                }
            }
            return {
                ...prev,
                rooms: newRooms
            };
        });
    };


  const handleAddRoom = () => {
    const newRoom: Room = {
      adultCount: 1,
      child: { childCount: 0, ages: [] }
    };
    setReservationData(prev => ({
      ...prev,
      rooms: [...prev.rooms, newRoom]
    }));
  };
    const handleAgesChange = (roomIndex: number, ageIndex: number, value: number) => {
        setReservationData(prev => {
            const newRooms = [...prev.rooms];
            const newAges = [...newRooms[roomIndex].child.ages];
            newAges[ageIndex] = value;
            newRooms[roomIndex] = {
                ...newRooms[roomIndex],
                child: {
                    ...newRooms[roomIndex].child,
                    ages: newAges
                }
            }
            return {
                ...prev,
                rooms: newRooms
            }

        })
    }
    // const addAges = (rooms: Room[]): Room[] => {
    //     return rooms.map(room => {
    //         if (room.child.count > 0 && !room.child.ages) {
    //             return {
    //                 ...room,
    //                 child: {
    //                     ...room.child,
    //                     ages: []
    //                 }
    //             };
    //         }
    //         return room;
    //     });
    // };

    console.log(reservationData);
    const handleDateChange = (field: keyof BookedDate, value: string) => {
        setDate(prevDate => ({
            ...prevDate,
            [field]: value
        }))
    }
    return (
        <div className='BookingForm'>
            <DateForm
                date={date}
                onDateChange={handleDateChange}
            />
            <StatBooking
                rooms={reservationData.rooms}
                handleInputClicked={handleInputClicked}
            />
            {inputClicked && <GuestForm rooms={reservationData.rooms} onGuestChange={handleGuestChange} />}
            <div>
                <button onClick={handleAddRoom}>Add new Room</button>
            </div>

        </div>
    )
}
