import React, { useEffect, useState } from 'react';
import { useBookingContext } from './ContexApplication';

interface AgeCartComponentProps {
    childrenCount: number;
    roomIndex: number;
}

export const AgeCartComponent: React.FC<AgeCartComponentProps> = ({ childrenCount, roomIndex }) => {
    const [localAges, setLocalAges] = useState<string[]>(Array(childrenCount).fill(""));
    const { bookingData, setBookingData, getAges, handleAgeByIndex } = useBookingContext();

    useEffect(() => {
        const newAges = getAges(localAges, childrenCount);
        setLocalAges(newAges);
    }, [childrenCount]);

    useEffect(() => {
        const roomsUpdate = handleAgeByIndex(roomIndex, localAges);
        const updatedRooms = { ...bookingData.rooms };
        updatedRooms[`Room ${roomIndex}`] = roomsUpdate;
        setBookingData({ ...bookingData, rooms: updatedRooms });
    }, [localAges, roomIndex]);

    const handleAgeChange = (index: number, value: string) => {
        const newAges = [...localAges];
        newAges[index] = value;
        setLocalAges(newAges);
    };

    return (
        <div className='AgeCartComponent'>
            {localAges.map((age, index) => (
                <select
                    key={index}
                    value={age}
                    onChange={(e) => handleAgeChange(index, e.target.value)}
                >
                    <option value="">--Select child {index + 1} Age--</option>
                    {[...Array(18)].map((_, optionIndex) => (
                        <option key={optionIndex} value={optionIndex.toString()}>
                            {optionIndex} years old
                        </option>
                    ))}
                </select>
            ))}
        </div>
    );
};
