import React from 'react';
import { Room } from '../interfaces/Room';
import { AgesForm } from './AgesForm';
import "../css/guestForm.css";

type field = "adultCount" | "childCount";
interface GuestFormProps {
    rooms: Map<number, Room>;
    onHandleGuestCountChange: (index: number, field: field, value: number) => void;
    onDeleteRoom: (index: number) => void;
    onChildAgesChange: (index: number, ages: number[]) => void;
    onAddRoom:()=>void;
}

export const GuestForm: React.FC<GuestFormProps> = (
    {
        rooms,
        onHandleGuestCountChange,
        onDeleteRoom,
        onChildAgesChange,
        onAddRoom,
    }) => {

    return (
        <div className='GuestForm ' style={{position:"absolute"}}>
            {Array.from(rooms.entries()).map(([index, room]) => (
                <form key={index} >
                    <input
                        type="number"
                        name="adultCount"
                        value={room.adultCount}
                        onChange={(e) => onHandleGuestCountChange(index, 'adultCount', Number(e.target.value))}
                    />
                    <input
                        type="number"
                        name="childCount"
                        value={room.child.childCount}
                        onChange={(e) => {
                            const newChildCount = Number(e.target.value);
                            onHandleGuestCountChange(index, 'childCount', newChildCount);
                            if (newChildCount === 0) {
                                onChildAgesChange(index, []);
                            }
                        }}

                    />
                    {room.child.childCount > 0 && <AgesForm
                        childCount={room.child.childCount}
                        onChildAges={(newAges) => onChildAgesChange(index, newAges)}
                    />
                    }
                    {index > 1 && <button type='button' onClick={() => onDeleteRoom(index)}>delete</button>}
                </form>

            ))}
            <button type='button' onClick={onAddRoom}>Add New Room</button>
        </div>
    );
};
