import React from 'react';
import { Room } from '../interfaces/Room';
import { useBookingContext } from './ContexApplication';
import "../css/handleguest.css";
interface HandleGuestProps {
    room: Room;
    roomIndex: number;
}
export const HandleGuest: React.FC<HandleGuestProps> = ({ room, roomIndex }) => {
    const { adultQuantityDecrement,
        adultQuantityIncrement,
        childrenQuantityDecrement,
        childrenQuantityIncrement,
        handleDeleteRoom
    }
        = useBookingContext();
    return (
        <section className='HandleGuestConteiner' >
            <p style={{ fontWeight: "bold", textAlign: "center" }} className='HandleGuestConteinerTitle'> Room  {roomIndex}</p>
            <div >


                <div style={{
                    display: "flex",
                    alignContent: "center",
                    alignItems: "center",
                    gap: "8.2rem",
                    marginBottom: "1em"
                }}>
                    <div>
                        <h4 >Adult</h4>
                    </div>

                    <div>
                        <button
                            style={{
                                width: "3rem",
                                height: "3rem",
                                border: "3px blue solid",
                                backgroundColor: "#fff",
                                borderRadius: "3rem"
                            }}
                            onClick={() => adultQuantityIncrement(roomIndex)}
                        >+
                        </button>

                        <input
                            type="text"
                            value={room.adultCount}
                            readOnly
                            style={{
                                maxWidth: "4rem",
                                textAlign: "center",
                                border: "none",
                                fontWeight: "bold",
                                padding: "1rem"
                            }}
                        />
                        <button
                            onClick={() => adultQuantityDecrement(roomIndex)}
                            disabled={room.adultCount === 1}
                            style={{
                                width: "3rem",
                                height: "3rem",
                                border: "3px blue solid",
                                backgroundColor: "#fff",
                                borderRadius: "3rem"
                            }}
                        >-
                        </button>
                    </div>
                </div>

                <div style={{
                    display: "flex",
                    alignContent: "center",
                    alignItems: "center",
                    gap: "8.2rem",
                    marginBottom: "1em"
                }}>
                    <h4>Children</h4>
                    <div>
                        <button
                            style={{
                                width: "3rem",
                                height: "3rem",
                                border: "3px blue solid",
                                backgroundColor: "#fff",
                                borderRadius: "3rem"
                            }}
                            onClick={() => childrenQuantityIncrement(roomIndex)}
                        >+
                        </button>

                        <input
                            type="text"
                            value={room.child.childCount}
                            readOnly
                            style={{
                                maxWidth: "4rem",
                                textAlign: "center",
                                border: "none",
                                fontWeight: "bold",
                                padding: "1rem"
                            }}

                        />
                        <button onClick={() => childrenQuantityDecrement(roomIndex)}
                            style={{
                                width: "3rem",
                                height: "3rem",
                                border: "3px blue solid",
                                backgroundColor: "#fff",
                                borderRadius: "3rem"
                            }}
                            disabled={room.child.childCount === 0}
                        >-
                        </button>

                    </div>

                </div>
            </div>
            {roomIndex > 1 &&
                <button
                    onClick={() => handleDeleteRoom(roomIndex)}

                > delete
                </button>
            }
        </section>
    )
}
