import React, { useState } from 'react'
import { Room } from '../interfaces/Room'
import { AgesForm } from './AgesForm';
type field = 'adultCount' | 'childCount';
interface GuestFormProps {
    onGuestChange: (roomIndex: number, field: field, value: number) => void;
    onDelete: (index: number) => void;
    onSendRoom:(rooms:Room[])=>void;
}
export const GuestForm: React.FC<GuestFormProps> = ({onGuestChange, onDelete, onSendRoom }) => {
    const [roomsData, setRoomsDate] = useState<Room[]>([{adultCount:1, child:{childCount:0, ages:[]}}]);
    const handleAddRoom = () =>{
        const newRoom = {
            adultCount:1,
            child:{
                childCount:0,
                ages:[]
            }
        }
        setRoomsDate([...roomsData, newRoom]);
        onSendRoom(roomsData);
    }
    console.log(roomsData, 'From Rooms')

    const handleAgesChange = (roomIndex: number, newAges: number[]) => {
        setRoomsDate(prevData => ({
            ...prevData,
            rooms: prevData.map((room, index) =>
                index === roomIndex ? { ...room, child: { ...room.child, ages: newAges } } : room
            )
        }));
    };


    return (
        <div className='GuestForm'>
            {
                roomsData.map((room, roomIndex) => (
                    <form key={roomIndex}>
                        <input
                            type="number"
                            value={room.adultCount}
                            onChange={(e) => onGuestChange(roomIndex, 'adultCount', Number(e.target.value))}
                        />
                        <input
                            type="number"
                            value={room.child.childCount}
                            onChange={(e) => onGuestChange(roomIndex, 'childCount', Number(e.target.value))}
                        />
                        {roomIndex > 0 && <button type='button' onClick={() => onDelete(roomIndex)}>Delete</button>}

                        {room.child.childCount > 0 &&
                            <AgesForm
                                childCount={room.child.childCount}
                                onAgesChange={(newAges) => handleAgesChange(roomIndex, newAges)}
                            />
                        }
                    </form>
                ))
            }
            <button onClick={handleAddRoom}>addRoom</button>
        </div>
    )
}
