import React, { useEffect, useState } from 'react';
import { useBookingContext } from './ContexApplication';
interface ChildrenAgesComponentProps {
    onChildrenCountChange: (value: number) => void;
}
export const ChildrenAgesComponent: React.FC = () => {
    const [childrenCount, setchildrenCount] = useState<number>(0);
    const { setBookingData } = useBookingContext();
    const handleChildrenCountIncrement = () => {
        setchildrenCount(prev => prev + 1);
        //onChildrenCountChange(childrenCount);
    }
    const handleChildrenCountDecrement = () => {
        setchildrenCount(prev => Math.max(prev - 1, 0))
        //onChildrenCountChange(childrenCount);
    }


    return (
        <section>
            {childrenCount > 0 &&
                <div>
                    {Array(childrenCount).fill(0)}
                </div>
            }
            <button onClick={handleChildrenCountIncrement}>+</button>
            <button onClick={handleChildrenCountDecrement}>-</button>
        </section>
    );
};
