import React, { useState } from 'react';
import { useBookingContext } from '../components/ContexApplication';
import { guest } from '../interfaces/guest';
interface UserFormProps {
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({ onNext, onPrevious }) => {
  const { setBookingData, bookingData } = useBookingContext();
  const [userData, setUserData] = useState<guest>()
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setBookingData(prev => ({
      ...prev,
      guest: userData
    }))
    onNext();
  };

  const formUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;


  };
  console.log(bookingData)
  return (
    <div className='UserFormConteiner'
    >
      <div className="userForm">

        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={userData?.name}
              onChange={formUserChange}
              className='user'
            />
          </div>
          <div>

            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={userData?.lastName}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={userData?.email}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={userData?.phone}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <button
            type="submit" onClick={handleSubmit}
          >Next
          </button>
          <button
            onClick={() => onPrevious()}>
            Previuos
          </button>
        </form>
      </div>
    </div>
  );
};
