import React from 'react'
import { Data } from '../interfaces/Data';
import { useBookingContext } from './ContexApplication';
import { BottonNext } from './BottonNext'
import "../css/SelectedRoomCart.css";
interface SelectedRoomCartProps {
    roomSelected: Data;
    handleNext: () => void;
}
export const SelectedRoomCart: React.FC<SelectedRoomCartProps> = ({ roomSelected, handleNext}) => {
    const { getGuestInfo } = useBookingContext();
    const StatGuestFormatted = getGuestInfo("stat");
    return (
        <div key={roomSelected.id} className='SelectedRoomItem'>
            <div className='SelectedImage'>
                <img src={roomSelected.src} alt={roomSelected.category} style={{ minWidth: "40rem", height: "20rem", objectFit: "cover" }} />
            </div>
            <div className='SelectedRoomsDescription'>
                <h2>{roomSelected.category}</h2>
                <p>Description: {roomSelected.description}</p>
                <p>Price: {roomSelected.price}$</p>
                <p>Max Guests: {roomSelected.maxGuest}</p>

            </div>
            <BottonNext onNextPage={handleNext}></BottonNext>
        </div>
    )
}
