import React from 'react'
import { useBookingContext } from './ContexApplication';
import { GuestCart } from './GuestCart';
import { AgeCartComponent } from './AgeCartComponent';
import "../css/roomManagemantComponent.css";
import { ButtonComponent } from './ButtonComponent';
export const RoomManagemantComponent: React.FC = () => {
    const { bookingData, handleAddNewRoom, handleDeleteRoom,
        handleGuestCountChange, updateAgesLenght, handleNextClick
    } = useBookingContext();
    console.log(bookingData);
    return (
        <section
            className='RoomManagemantComponent'
        >
        <div className='BookingHeaderTitle'>GUEST  AND ROOM SELECTION
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room]) => (
                <section
                    key={roomIndex}
                    className='Guest'
                    style={{ display: "flex",backgroundColor:"rgb(112, 106, 106)"}}>
                    <section>
                        <div>

                            <GuestCart
                                onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                                key={roomIndex}
                                onChildrenCountChange={(count) => updateAgesLenght(roomIndex, count)}
                            />
                        </div>
                        <div >

                            {room.child.childCount > 0 &&
                                <AgeCartComponent
                                    childrenCount={room.child.childCount}
                                    roomIndex={roomIndex}
                                />
                            }
                        </div>
                    </section>
                    {roomIndex > 1 &&
                        <div className='ButtonDelete' style={{ marginLeft: "1em" }}>
                            <ButtonComponent
                                onClickButton={() => handleDeleteRoom(roomIndex)}
                                padding='0.6em'
                            >delete
                            </ButtonComponent>
                        </div>
                    }
                </section>
            ))}
            <div  style={{marginTop:"0.2rem", display:"flex", alignItems:"center", gap:"8.3rem"}}>
                <ButtonComponent
                    onClickButton={handleAddNewRoom}
                    padding='0.6rem'
                    minWidth='6rem'
                >Add Room
                </ButtonComponent>

                <ButtonComponent
                    onClickButton={handleNextClick}
                    padding='0.6rem'
                    minWidth='6rem'
                >
                    Next
                </ButtonComponent>
            </div>
            </div>
        </section>
    )
}
