import React from 'react'
import { Room } from '../interfaces/Room'
interface SelectAgesProps {
    rooms: Map<number, Room>
    OnAgesChange: (roomIndex: number, AgesIndex: number, value: number) => void;
}
export const SelectAges: React.FC<SelectAgesProps> = ({ rooms, OnAgesChange }) => {

    return (
        <div className='SelectAges'>
            {Array.from(rooms.entries()).map(([indexIndex, room]) => (
                <div>
                    {room.child.childCount > 0 && (
                        <div className='ChildForm'
                            style={
                                {
                                    backgroundColor: "#1a2a3a",
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "center",
                                    justifyContent: "center",
                                    paddingTop: "1em",
                                    paddingBottom: "1em"
                                }
                            }
                        >
                            {Array.from({ length: room.child.childCount }).map((_, key) => (

                                <select name="" id="" key={key}
                                    value={room.child.ages[key] !== undefined ? room.child.ages[key].toString() : ""}
                                    onChange={(e) => OnAgesChange(indexIndex, key, Number(e.target.value))}
                                >
                                    <option value="">child {key + 1} Select Age</option>
                                    <option value={1}>1</option>
                                    <option value={2}>2</option>
                                    <option value={3}>3</option>
                                    <option value={4}>4</option>
                                    <option value={5}>5</option>
                                    <option value={6}>6</option>
                                    <option value={7}>7</option>
                                    <option value={8}>8</option>
                                    <option value={9}>9</option>
                                    <option value={10}>10</option>
                                    <option value={11}>11</option>
                                    <option value={12}>12</option>
                                    <option value={13}>13</option>
                                    <option value={14}>14</option>
                                </select>

                            ))
                            }
                        </div>
                    )
                    }
                </div>
            ))}
        </div>
    )
}
