import React from 'react'
import { Room } from '../interfaces/Room'
interface GuestFormProps {
    rooms: Room[];
    onGuestChange:()=>void;
}
export const GuestForm: React.FC<GuestFormProps> = ({rooms, onGuestChange}) => {

    return (
        <div className='GuestForm'>
            {rooms.map((room , roomIndex)=>(
                <form  key={roomIndex}>
                    <input 
                        type="number"
                        value={room.adultCount}
                        onChange={onGuestChange}
                    />
                    <input  
                        type="number" 
                        value={room.child.count}
                        onChange={onGuestChange}
                    />
                </form>
            ))}
        </div>
    )
}
