import React, { useState } from 'react'
import { date } from '../interfaces/BookedDate'
import { DateForm } from '../components/DateForm';
import { StatBooking } from '../components/StatBooking';
import { GuestForm } from '../components/RoomDataForm';
import { Room } from '../interfaces/Room';
import { resevationData } from '../interfaces/ReservationData';
import "../css/bookingForm.css"
type field = "adultCount" | "childCount";
interface BookedFormProps{
    onDataChange:(data:resevationData)=>void;
    onNext:()=>void;
}
export const BookingForm: React.FC<BookedFormProps> = ({onDataChange, onNext}) => {
    const today = new Date().toISOString().split('T')[0];
    const [date, setDate] = useState<date>({ checkIn: today, checkOut: today });
    const [inputClicked, setInputClicked] = useState<boolean>(false);
    const inicialRooms = new Map<number, Room>();
    inicialRooms.set(1, { adultCount: 1, child: { childCount: 0, ages: [] } });

    const inicialReservationData: resevationData = {
        date:  { checkIn: today, checkOut: today },
        rooms: inicialRooms,
        guest: { name: '', lastName: '', email: '', phone: '' }
    }
    const [reservationData, setReservationData] = useState<resevationData>(inicialReservationData);
    const hanledSubmit = () =>{
        onDataChange(reservationData);
        onNext();
    }

    const addNewRoom = () => {
        const newRoom: Room = {
            adultCount: 1,
            child: { childCount: 0, ages: [] }
        }
        const newRooms = new Map(reservationData.rooms);
        newRooms.set(reservationData.rooms.size + 1, newRoom);
        setReservationData(prev => ({
            ...prev,
            rooms: newRooms
        }))
    }
    const handleGuestCountChange = (index: number, field: field, value: number) => {
        setReservationData(prev => {
            const updateRooms = new Map(prev.rooms);
            const updateRoom = updateRooms.get(index);
            if (updateRoom) {
                if (field === 'adultCount') {
                    updateRoom.adultCount = value;
                } else if (field === 'childCount') {
                    updateRoom.child.childCount = value;
                }
                updateRooms.set(index, updateRoom);
                return {
                    ...prev,
                    rooms: updateRooms
                }

            }
            return prev
        });
    }
    const handleChildAgeChange = (index: number, ages: number[]) => {
        setReservationData(prev => {
            const updateRooms = new Map(prev.rooms);
            const roomToUpdate = updateRooms.get(index);
            if (roomToUpdate) {
                roomToUpdate.child.ages = ages;
                updateRooms.set(index, roomToUpdate);
                return {
                    ...prev,
                    rooms: updateRooms
                }
            }
            return prev
        })
    }
    const handleDeleteRoom = (index: number) => {
        setReservationData(prev => {
            const updateRooms = new Map(reservationData.rooms);
            updateRooms.delete(index);
            return {
                ...prev,
                rooms: updateRooms
            }
        })
    }
    const handleInputClicked = () => {
        setInputClicked(true);
    }

    const handleDateChange = (field: keyof date, value: string) => {
        setDate(prevDate => ({
            ...prevDate,
            [field]: value
        }))
    }
    return (
        <div className='BookingForm flex'>
            <DateForm
                date={date}
                onDateChange={handleDateChange}
            />
            <StatBooking
                rooms={reservationData.rooms}
                handleInputClicked={handleInputClicked}
            />
            {
                inputClicked && <div>
                    <GuestForm
                        rooms={reservationData.rooms}
                        onHandleGuestCountChange={handleGuestCountChange}
                        onDeleteRoom={handleDeleteRoom}
                        onChildAgesChange={handleChildAgeChange}
                    />
                    <button onClick={() => setInputClicked(false)}>Next</button>
                    <button onClick={addNewRoom}> add room</button>
                </div>
            }
            <div className='BookingBotton'></div>
            <button onClick={hanledSubmit}>booking</button>
        </div>
    )
}
