import React, { useState } from 'react';
import "../css/guestCart.css";
type field = "adultCount" | "childCount";

interface GuestCartProps {
    onGuestChange: (field: field, value: number) => void;
    onChildrenCountChange: (count: number) => void;
}
export const GuestCart: React.FC<GuestCartProps> = ({ onGuestChange, onChildrenCountChange }) => {
    const [childrenCount, setChildrenCount] = useState<number>(0);
    const [adultCount, setAdultCount] = useState<number>(1);
    // Modificar  GuestCart=>children y su ParentComponent.
    const handleChildrenCountIncrement = () => {
        setChildrenCount(prev => prev + 1);
        onGuestChange('childCount', childrenCount + 1);
        onChildrenCountChange(childrenCount + 1);
    }

    const handleChildrenCountDecrement = () => {
        setChildrenCount(prev => Math.max(prev - 1, 0));
        onGuestChange('childCount', Math.max(childrenCount - 1, 0));
        onChildrenCountChange(Math.max(childrenCount - 1, 0));
    }

    const handleAdultCountIncrement = () => {
        setAdultCount(prev => prev + 1);
        onGuestChange('adultCount', adultCount + 1);
    }

    const handleAdultCountDecrement = () => {
        setAdultCount(prev => Math.max(prev - 1, 1));
        onGuestChange('adultCount', Math.max(adultCount - 1, 1));
    }

    const handleAdultCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newValue = Number(e.target.value);
        if (newValue > 0) {
            setAdultCount(newValue);
            onGuestChange('adultCount', newValue);
        } else {
            Error("The valor of Adult Can't be less that Zero")
        }
    }

    const handleChildrenCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const newValue = Number(e.target.value);
        setChildrenCount(newValue);
        onGuestChange('childCount', newValue);
    }
    return (
        <section className='GuestCart flex'>
            <div>
                <input
                    type="number"
                    value={adultCount}
                    onChange={handleAdultCountChange}
                    style={{maxWidth:"2rem"}}
                />
                <button
                    onClick={handleAdultCountIncrement}
                    className='AdultIncrementBntLeft'
                    style={{width:"2rem", height:"2rem", border:"none", borderRadius:"3rem", cursor:"pointer"}}
                >+
                </button>
                <button
                    className='AdultDecrementBntRigth'
                    onClick={handleAdultCountDecrement}
                    style={{width:"2rem", height:"2rem", border:"none", borderRadius:"3rem", cursor:"pointer"}}
                >-
                </button>
            </div>
            <div>
                <input
                    type="number"
                    value={childrenCount}
                    onChange={handleChildrenCountChange}
                />
                <button
                    className='ChildrenIncrementBntLeft'
                    onClick={handleChildrenCountIncrement}
                    style={{width:"2rem", height:"2rem", border:"none", borderRadius:"3rem", cursor:"pointer"}}
                >+
                </button>
                <button
                    className='ChildrenDecrementBntRigth'
                    onClick={handleChildrenCountDecrement}
                    style={{width:"2rem", height:"2rem", border:"none", borderRadius:"3rem", cursor:"pointer"}}
                >-
                </button>
            </div>
        </section>

    );
}
