import React, { useEffect, useState } from 'react'
import { Room } from '../interfaces/Room'
import { AgesForm } from './AgesForm';
type field = 'adultCount' | 'childCount';
interface GuestFormProps {
    onSendRoom: (rooms: Room[]) => void;
}
export const GuestForm: React.FC<GuestFormProps> = ({ onSendRoom }) => {
    const [roomsData, setRoomsDate] = useState<Room[]>([{ adultCount: 1, child: { childCount: 0, ages: [] } }]);
    const handleAddRoom = () => {
        const newRoom = {
            adultCount: 1,
            child: {
                childCount: 0,
                ages: []
            }
        }
        setRoomsDate([...roomsData, newRoom]);

    }
    useEffect(() => {
        onSendRoom(roomsData);
    }, [roomsData])
    console.log(roomsData, 'From Rooms')

    const handleAgesChange = (roomIndex: number, newAges: number[]) => {
        setRoomsDate(prevData => ({
            ...prevData,
            rooms: prevData.map((room, index) =>
                index === roomIndex ? { ...room, child: { ...room.child, ages: newAges } } : room
            )
        }));
    };
    const handleDeleteRoom = (roomIndex: number) => {
        setRoomsDate(prevRooms => prevRooms.filter((_, index) => index !== roomIndex))
    }
    const handleGuestChange = (roomIndex: number, field: field, value: number) => {
        setRoomsDate(prev => {
            const newRooms = [...prev];
            if (field === 'adultCount') {
                newRooms[roomIndex] = {
                    ...newRooms[roomIndex],
                    [field]: value
                };
            } else if (field === 'childCount') {
                newRooms[roomIndex] = {
                    ...newRooms[roomIndex],
                    child: { childCount: value, ages: [] }
                }
            }
            return {
                ...prev,
                rooms: newRooms
            };
        });
    };


    return (
        <div className='GuestForm'>
            {
                roomsData.map((room, roomIndex) => (
                    <form key={roomIndex}>
                        <input
                            type="number"
                            value={room.adultCount}
                            onChange={(e) => handleGuestChange(roomIndex, 'adultCount', Number(e.target.value))}
                        />
                        <input
                            type="number"
                            value={room.child.childCount}
                            onChange={(e) => handleGuestChange(roomIndex, 'childCount', Number(e.target.value))}
                        />
                        {room.child.childCount > 0 &&
                            <AgesForm
                                childCount={room.child.childCount}
                                onAgesChange={(newAges) => handleAgesChange(roomIndex, newAges)}
                            />
                        }
                        {roomIndex > 0 && <button type='button' onClick={() => handleDeleteRoom(roomIndex)}>Delete</button>}
                    </form>
                ))
            }
            <button onClick={handleAddRoom}>addRoom</button>

        </div>
    )
}
