import React, { useState } from 'react';
import { useBookingContext } from '../components/ContexApplication';


interface UserFormProps {
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({ onNext, onPrevious }) => {
  const {bookingData,setBookingData } = useBookingContext();

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onNext();
  };

  const formUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setBookingData(prev => ({
        ...prev,
        guest: {
            ...(prev.guest || {}),
            [name]: value
        }
    }));
};

  return (
    <div className='UserFormConteiner'>
      <div className="userForm">
        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={bookingData.guest?.name || ''}
              onChange={formUserChange}
              className='user'
            />
          </div>
          <div>
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={bookingData.guest?.lastName || ''}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={bookingData.guest?.email || ''}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={bookingData.guest?.phone || ''}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <button type="submit">Next</button>
          <button type="button" onClick={onPrevious}>Previuos</button>
        </form>
      </div>
    </div>
  );
};
