import React, { useState } from 'react';
import { guest } from '../interfaces/guest';

interface UserFormProps {
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({onNext, onPrevious }) => {
  const [formGuest, setFormGuest] = useState<guest>({
    name: '',
    lastName: '',
    email: '',
    phone: '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onNext();
  };

  const formUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormGuest((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className='UserFormConteiner'

     >
      <div className="userForm">

        <form onSubmit={handleSubmit}>
          <div>

            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formGuest.name}
              onChange={formUserChange}
              className='user'
            />
          </div>
          <div>

            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={formGuest.lastName}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formGuest.email}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formGuest.phone}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
            <button 
                type="submit" 
              >Next
            </button>
            <button 
                onClick={() => onPrevious()}>
                  Previuos
                </button>
        </form>
      </div>
    </div>
  );
};
