import React, { useEffect, useState } from 'react';
import { Room } from '../interfaces/Room';

interface SelectAgesProps {
    room: Room;
    onAgeInputChange: (index: number, value: number) => void;
}

export const SelectAges: React.FC<SelectAgesProps> = ({ room, onAgeInputChange }) => {
    const [ages, setAges] = useState<number[]>(Array.from({ length: room.child.childCount }, (_, i) => room.child.ages[i] || 1));

    useEffect(() => {
        // Ensure that the ages array is of the correct length
        const validAges = room.child.ages.slice(0, room.child.childCount).concat(Array(Math.max(0, room.child.childCount - room.child.ages.length)).fill(1));
        setAges(validAges);
    }, [room.child.childCount, room.child.ages]);

    return (
        <div className='SelectAges'>
            {room.child.childCount > 0 && (
                ages.map((value, index) => (
                    <select 
                        name="" 
                        id="" 
                        key={index}
                        value={value}
                        onChange={(e) => onAgeInputChange(index, Number(e.target.value))}
                        aria-label={`Select age for child ${index + 1}`}
                    >
                        <option value=''>Select child {index + 1} Age</option>
                        {[...Array(14)].map((_, age) => (
                            <option key={age + 1} value={age + 1}>{age + 1}</option>
                        ))}
                    </select>
                ))
            )}
        </div>
    );
};
