import React, { useState } from 'react';
import { useBookingContext } from '../components/ContexApplication';
import { guest } from '../interfaces/guest';
import "../css/userForm.css"

interface UserFormProps {
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({ onNext, onPrevious }) => {
  const { bookingData, setBookingData } = useBookingContext();
  const [user, setUser] = useState<guest>({
    name: '', lastName: '', email: '', phone: ''
  })
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setBookingData(prev => ({
      ...prev,
      guest: user
    }));
    onNext();
  };

  const handleUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setUser(prev => ({
      ...prev,
      [name]: value
    }))
  };
  console.log(bookingData);
  return (
    <div className='UserFormConteiner'>
      <div className="userForm">
        <form onSubmit={handleSubmit}>
          <div className='Title'>BOOKING USER FORM</div>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={user.name}
              onChange={handleUserChange}
              className='user'
              required
            />
          </div>
          <div>
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={user.lastName}
              onChange={handleUserChange}
              required
              className='user'
            />
          </div>
          <div>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={user.email}
              onChange={handleUserChange}
              required
              className='user'
            />
          </div>
          <div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={user.phone}
              onChange={handleUserChange}
              required
              className='user'
            />
          </div>
          <button type="submit" onClick={handleSubmit}>Submit</button>
          <button
            type="button"
            onClick={onPrevious}
          >Previuos
          </button>
        </form>
      </div>
    </div>
  );
};
