import React, { useEffect, useState } from 'react'
import { Room } from '../interfaces/Room'
// Ese Componente deberia recibir un array de edades de los Guest
// Despues de editarlar el array debe ser enviado a su componente padre, cada ves que se edite la edad en 
// cualquier habitacion se deberia volver a enviar los datos al componente padre.
interface SelectAgesProps {
    childCount:number;
    onAges: (dataAges: number[]) => void;
}
export const SelectAges: React.FC<SelectAgesProps> = ({ childCount, onAges }) => {
    const [childAges, setChildAges] = useState<number[]>(Array.from({ length: childCount }));
    useEffect(() => {
        setChildAges(Array.from({ length: childCount }));
    }, [childCount])

    const handleAgeChange = (index: number, value: number) => {
        setChildAges(prev => {
            const updateAges = [...prev];
            updateAges[index] = value;
            return updateAges
        })

        onAges(childAges);
    }
    return (
        <section>
            {childAges.map((value, index) => (
                <select
                    name="Ages"
                    id="Ages"
                    key={index}
                    value={value}
                    onChange={(e) => handleAgeChange(index, Number(e.target.value))}
                >
                    <option value="">Select child {index} Ages</option>
                    {[...Array(18)].map((_, option) => (
                        <option
                            key={option}
                        >
                            {option}
                        </option>
                    ))}
                    {value}
                </select>
            ))}
        </section>
        // <div className='SelectAges'>
