import React, { useState } from 'react';
import { guest } from '../interfaces/guest';

interface UserFormProps {
  onUserChange: (guest: guest) => void;
  onNext: () => void;
  onPrevious:()=>void;
}

export const UserForm: React.FC<UserFormProps> = ({ onUserChange, onNext }) => {
  const [formGuest, setFormGuest] = useState<guest>({
    name: '',
    lastName: '',
    email: '',
    phone: '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onUserChange(formGuest);
    onNext();
  };

  const formUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormGuest((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="userForm">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formGuest.name}
          onChange={formUserChange}
          required
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formGuest.lastName}
          onChange={formUserChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formGuest.email}
          onChange={formUserChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone"
          value={formGuest.phone}
          onChange={formUserChange}
          required
        />
        <button type="submit">Next</button>
      </form>
    </div>
  );
};
