import React from 'react';
import { useBookingContext } from './ContexApplication';
import { GuestCart } from './GuestCart';
import { AgeCartComponent } from './AgeCartComponent';

export const RoomManagementComponent: React.FC = () => {
    const {
        bookingData,
        handleAddNewRoom,
        handleDeleteRoom,
        handleGuestCountChange,
        updateAgesLength,
        handleNextClick
    } = useBookingContext();

    console.log(bookingData);

    return (
        <section className='RoomManagementComponent'>
            {Object.entries(bookingData.rooms).map(([roomKey, roomMap]) => {
                const roomIndex = parseInt(roomKey.split(' ')[1], 10);
                const room = roomMap.get(roomIndex);
                if (!room) return null;
                return (
                    <section key={roomIndex}>
                        <GuestCart
                            onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                            key={roomIndex}
                            onChildrenCountChange={(count) => updateAgesLength(roomIndex, count)}
                        />
                        {roomIndex > 1 && <button onClick={() => handleDeleteRoom(roomIndex)}>Delete</button>}
                        <section>
                            {room.child.childCount > 0 &&
                                <AgeCartComponent
                                    childrenCount={room.child.childCount}
                                    roomIndex={roomIndex}
                                />
                            }
                        </section>
                    </section>
                );
            })}
            <button onClick={handleAddNewRoom}>Add New Room</button>
            <button onClick={handleNextClick}>Next</button>
        </section>
    );
};
