import React from 'react'
import { useBookingContext } from './ContexApplication';
import { GuestCart } from './GuestCart';
import { AgeCartComponent } from './AgeCartComponent';
import "../css/roomManagemantComponent.css";
interface RoomManagemantComponentPrpos {
    isSchildrenMenuDisplay: () => void;
}
export const RoomManagemantComponent: React.FC<RoomManagemantComponentPrpos> = ({ isSchildrenMenuDisplay }) => {
    const { bookingData, handleAddNewRoom, handleDeleteRoom,
        handleGuestCountChange, checkAges, setBookingData
    } = useBookingContext();
    const childrenQuantityIncrement = (index: number) => {
        setBookingData(prev => {
            const currentRoomsState = new Map(prev.rooms);
            const roomToUpdate = currentRoomsState.get(index);
            if (roomToUpdate) {
                roomToUpdate.child.childCount += 1;
            }
            return {
                ...prev,
                rooms: currentRoomsState
            }
        })
    }

    // const handleGuestCountChanges = (field: field, index: number, value: number) => {
    //     setBookingData(prev => {
    //         const updateRooms = new Map(prev.rooms);
    //         const GuestUpdate = updateRooms.get(index);
    //         if (GuestUpdate) {
    //             if (field === 'adultCount') {
    //                 GuestUpdate.adultCount = value;
    //             } else if (field === 'childCount') {
    //                 GuestUpdate.child.childCount = value
    //             }
    //             updateRooms.set(index, GuestUpdate);
    //             return {
    //                 ...prev,
    //                 rooms: updateRooms
    //             }
    //         }
    //         return prev;

    //     });
    // }
    const handleNextClick = () => {
        const isValid = checkAges();
        if (!isValid) {
            alert("Please ensure all child ages are selected.");
        } else {
            isSchildrenMenuDisplay();
        }
    }



    console.log(bookingData);
    return (
        <section
            className='RoomManagemantComponent flex'
            style={{ border: "3px #000 solid" }}
        >
            <h1 style={{ textAlign: "center" }}>Guest And Room Selection</h1>
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room]) => (
                <section
                    key={roomIndex}
                    className='Guest'
                    style={{
                        padding: "0.5em",
                        backgroundColor: "#fff"

                    }}

                >
                    {room.adultCount}
                    {room.child.childCount}
                    <p style={{ fontWeight: "bold", marginBottom: "1rem" }}>Room  {roomIndex}</p>
                    <div style={{ display: "flex", flexDirection: "row", gap: "1rem" }}>

                        <GuestCart
                            onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                            key={roomIndex}
                        />
                        <button  onClick={()=>childrenQuantityIncrement(roomIndex)}>-</button>
                        <button  >+</button>
                        {roomIndex > 1 &&
                            <div>

                                <button
                                    onClick={() => handleDeleteRoom(roomIndex)}
                                    style={{ padding: "0.6em", fontWeight: "bold", border: "none", borderRadius: "3rem", cursor: "pointer" }}
                                > delete
                                </button>
                            </div>
                        }
                    </div>
                    {room.child.childCount > 0 &&
                        <div style={{ maxWidth: "10rem", padding: "1em" }}>
                            <AgeCartComponent
                                childrenCount={room.child.childCount}
                                roomIndex={roomIndex}
                            />
                        </div>
                    }
                </section>

            ))}

            <div style={{ display: "flex", justifyContent: "space-between" }}>
                <button
                    style={{ padding: "0.5rem", border: "none", minWidth: "10rem", fontWeight: "bold", cursor: "pointer" }}
                    onClick={handleAddNewRoom}
                >Add Room
                </button>
                <button
                    style={{ padding: "0.5rem", border: "none", minWidth: "10rem", fontWeight: "bold", cursor: "pointer" }}
                    onClick={handleNextClick}
                >
                    Next
                </button>
            </div>


        </section>
    )
}
