import React, { useState } from 'react';
import { useBookingContext } from '../components/ContexApplication';
import { guest } from '../interfaces/guest';
import "../css/userForm.css"

interface UserFormProps {
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({ onNext, onPrevious }) => {
  const { bookingData, setBookingData } = useBookingContext();
  const [error, setError] = useState<Partial<guest>>({});
  const [user, setUser] = useState<guest>({
    name: '', lastName: '', email: '', phone: ''
  })

  const handleSubmit = () => {

    setBookingData(prev => ({
      ...prev,
      guest: user
    }));
    onNext();

  };
  const validationForm = (): boolean => {
    const newErrors: Partial<guest> = {};
    if (!user.phone) newErrors.name = 'Name is requered';
    if (!user.lastName) newErrors.lastName = 'LastName requered';
    if (!user.email) newErrors.email = 'Email requered';
    if (!user.phone) newErrors.phone = 'Phone requered';
    setError(newErrors);
    return Object.keys(newErrors).length === 0;
  }
  const handleUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setUser(prev => ({
      ...prev,
      [name]: value
    }))

  };
  console.log(bookingData);
  return (
    <div className='UserFormConteiner'>
      <div className="userForm">
        <form  >
          <div className='Title'>BOOKING USER FORM</div>
          <div>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={user.name}
              onChange={handleUserChange}
              className='user'
              required
            />
            {error.name && <span>{error.name}</span>}
          </div>
          <div>
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={user.lastName}
              onChange={handleUserChange}
              required
              className='user'
            />
            {error.lastName && <span>{error.lastName}</span>}
          </div>
          <div>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={user.email}
              onChange={handleUserChange}
              required
              className='user'
            />
            {error.email && <span>{error.email}</span>}
          </div>
          <div>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={user.phone}
              onChange={handleUserChange}
              required
              className='user'
            />
            {error.phone && <span>{error.phone}</span>}
          </div>
          <button type="submit" onClick={handleSubmit}>Submit</button>
          <button
            type="button"
            onClick={onPrevious}
          >Previuos
          </button>
        </form>
      </div>
    </div>
  );
};
