import React from 'react';
import { Room } from '../interfaces/Room';
import { useBookingContext } from './ContexApplication';
import "../css/handleguest.css";
interface HandleGuestProps {
    room: Room;
    roomIndex: number;
}
export const HandleGuest: React.FC<HandleGuestProps> = ({ room, roomIndex }) => {
    const { adultQuantityDecrement,
        adultQuantityIncrement,
        childrenQuantityDecrement,
        childrenQuantityIncrement,
        handleDeleteRoom
    }
        = useBookingContext();
    return (
        <section className='HandleGuestConteiner' >
            <p style={{ fontWeight: "bold", textAlign:"center" }} className='HandleGuestConteinerTitle'> Room  {roomIndex}</p>
            <div  style={{display:"flex", 
                        alignContent:"center",
                        alignItems:"center", 
                        justifyContent:"space-between",
                        marginBottom:"1em"
                    }}>
                <div>
                <h4 >Adult</h4>
                </div>


                <button
                    onClick={() => adultQuantityIncrement(roomIndex)}
                >+
                </button>

                <input
                    type="text"
                    value={room.adultCount}
                    readOnly
                    style={{maxWidth:"6rem", textAlign:"center"}}
                />
                <button
                    onClick={() => adultQuantityDecrement(roomIndex)}
                    disabled={room.adultCount === 1}
                >-
                </button>
            </div>
            <div style={{display:"flex", 
                        alignContent:"center",
                        alignItems:"center", 
                        justifyContent:"space-between",
                        marginBottom:"1em"
                    }}>

                <div>
                <h4>Children</h4>
                </div>
                <button
                    onClick={() => childrenQuantityIncrement(roomIndex)}
                >+
                </button>

                <input
                    type="text"
                    value={room.child.childCount}
                    readOnly
                    style={{maxWidth:"6rem", textAlign:"center"}}
                />
                <button onClick={() => childrenQuantityDecrement(roomIndex)}
                    disabled={room.child.childCount === 0}
                >-
                </button>

            </div>


            {roomIndex > 1 &&
                <button
                    onClick={() => handleDeleteRoom(roomIndex)}

                > delete
                </button>
            }
        </section>
    )
}
