import React, { useEffect, useState } from 'react'
import "../css/agesForm.css";
interface AgesFormProps {
    childCount: number;
    onChildAges: (newAges: number[]) => void;
}
export const AgesForm: React.FC<AgesFormProps> = ({ childCount, onChildAges }) => {
    const [childAges, setChildAges] = useState<number[]>(Array(childCount).fill(0));
    const [currenAge, setCurrentAge] = useState<number>(0)
    useEffect(() => {
        setChildAges(Array(childCount).fill(0))
    }, [childCount])
    const handleChildAgeChange = (ageIndex: number, newAge: number) => {
        const newAges = [...childAges];
        newAges[ageIndex] = newAge;
        setChildAges(newAges);
        onChildAges(newAges);
    }

    // const handleChildAgeChangeMap = (ageId:number, value:number) =>{
    //     const updateRooms = childAges.map((age, index) => index === ageId ? value: age);
    //     setChildAges(updateRooms);
    //     onChildAges(updateRooms);
    // }

    return (
        <div className='AgesForm' >
            {childAges.map((age, index) => (
                <input
                    key={index}
                    type="number"
                    value={age}
                    onChange={(e) => handleChildAgeChange(index, Number(e.target.value))}
                />
            ))
            }
            <button onClick={()=>setCurrentAge(prevAge=> prevAge + 1)}>+</button>
            <button onClick={()=>setCurrentAge(prevAge=> prevAge - 1)}>-</button>
        </div>
    )
}
