import React from 'react';
import { Room } from '../interfaces/Room';
type field = "adultCount" | "childCount";
interface GuestCartProps {
    room: Room;
    onGuestChange: (field:field, value: number) => void;
}
export const GuestCart: React.FC<GuestCartProps> = ({ room, onGuestChange }) => {
    return (
        <section className='GuestCart'>
            <input
                type="number"
                value={room.adultCount}
                onChange={(e) => onGuestChange("adultCount",Number(e.target.value))}
            />
            <input
                type="number"
                value={room.child.childCount}
                onChange={(e) => onGuestChange("childCount",Number(e.target.value))}
            />
        </section>
    )
}
