import React, { useEffect, useState, useRef } from 'react';

interface SelectAgesProps {
    childCount: number;
    onAges: (dataAges: number[]) => void;
}

export const SelectAges: React.FC<SelectAgesProps> = ({ childCount, onAges }) => {
    const [childAges, setChildAges] = useState<number[]>(Array.from({ length: childCount }));
    const prevChildAgesRef = useRef<number[]>(childAges);

    useEffect(() => {
        setChildAges(Array.from({ length: childCount }));
    }, [childCount]);

    useEffect(() => {
        // Only call onAges if childAges has actually changed
        if (prevChildAgesRef.current !== childAges) {
            onAges(childAges);
            prevChildAgesRef.current = childAges;
        }
    }, [childAges, onAges]);

    const handleAgeChange = (index: number, value: number) => {
        setChildAges(prev => {
            const updateAges = [...prev];
            updateAges[index] = value;
            return updateAges;
        });
    };

    return (
        <section>
            {childAges.map((value, index) => (
                <select
                    name="Ages"
                    id="Ages"
                    key={index}
                    value={value}
                    onChange={(e) => handleAgeChange(index, Number(e.target.value))}
                >
                    <option value="">Select child {index + 1} Ages</option>
                    {[...Array(18)].map((_, optionIndex) => (
                        <option key={optionIndex} value={optionIndex}>
                            {optionIndex}
                        </option>
                    ))}
                </select>
            ))}
        </section>
    );
};
