import React, { useMemo } from 'react';
import { useBookingContext } from './ContexApplication';

interface ShowRoomInfoComponentProps {
    onClickedInput: (input: boolean) => void;
}

export const ShowRoomInfoComponent: React.FC<ShowRoomInfoComponentProps> = ({ onClickedInput }) => {
    const { bookingData } = useBookingContext();

    // Calculate the total number of adults
    const adultSum = useMemo<number>(() => 
        Object.values(bookingData.rooms).reduce((sum, roomMap) => 
            sum + Array.from(roomMap.values()).reduce((roomSum, room) => roomSum + room.adultCount, 0), 0), 
        [bookingData.rooms]
    );

    // Calculate the total number of children
    const childSum = useMemo<number>(() => 
        Object.values(bookingData.rooms).reduce((sum, roomMap) => 
            sum + Array.from(roomMap.values()).reduce((roomSum, room) => roomSum + room.child.childCount, 0), 0), 
        [bookingData.rooms]
    );

    // Calculate the total number of rooms
    const roomsSum = useMemo<number>(() => 
        Object.keys(bookingData.rooms).length, 
        [bookingData.rooms]
    );

    // Determine the correct pluralization for the titles
    const adultTitle = adultSum  > 1 ? 'adults' : 'adult';
    const childTitle = childSum  > 1 ? 'children' : 'child';
    const roomTitle  =  roomsSum > 1 ? 'rooms' : 'room';

    // Format the summary string
    const formattedStat = childSum === 0 ?
        `${adultSum} ${adultTitle}, ${roomsSum} ${roomTitle}` :
        `${adultSum} ${adultTitle}, ${childSum} ${childTitle}, ${roomsSum} ${roomTitle}`;

    return (
        <section className='ShowRoomInfoComponent'>
            <input
                type="text"
                readOnly
                value={formattedStat}
                onClick={() => onClickedInput(true)}
            />
        </section>
    );
};
