import React,{useState} from 'react'
import { Data } from '../interfaces/Data';
import "../css/SelectedRoomCart.css";

interface SelectedRoomCartProps {
    roomSelected: Data;
    onSelectedRoom: () => void;
    text: string;
    currentImage: number;
}
export const SelectedRoomCart: React.FC<SelectedRoomCartProps> = ({ roomSelected, onSelectedRoom, text, currentImage }) => {
    const [imageIndex, setImageIndex] = useState<number>(0);
    const handleNextIndex = () => {
        let nextIndex = (imageIndex + 1) % roomSelected.images.length;
        setImageIndex(nextIndex);
    }

    const handlePrevIndex = () => {
        let prevIndex = (imageIndex - 1 + roomSelected.images.length) % roomSelected.images.length;
       setImageIndex(prevIndex);
    }
    return (
        <div key={roomSelected.id} className='SelectedRoomItem  grid'>
            <div className='SelectedImage'>
                <img src={roomSelected.images[imageIndex]} alt={roomSelected.category} style={{ minWidth: "40rem", height: "20rem", objectFit: "cover" }} />
                {imageIndex}
                <button onClick={handleNextIndex}>next</button>
                <button onClick={handlePrevIndex}>prev</button>
            </div>
            <div className='SelectedRoomsDescription'>
                <h2>{roomSelected.category}</h2>
                <p>Description: {roomSelected.description}</p>
                <p>Price: {roomSelected.price}$</p>
                <p>Max Guests: {roomSelected.maxGuest}</p>
                <div >
                    <button
                        onClick={onSelectedRoom}
                        style={{
                            padding: "0.7rem",
                            border: "none",
                            marginTop: "4em",
                            cursor: "pointer",
                            minWidth: "8rem",
                            fontWeight: "bold",
                            borderRadius: "3rem"
                        }}
                    >{text}</button>
                </div>
            </div>
        </div>
    )
}
