import React, { useState } from 'react'
import { date } from '../interfaces/BookedDate'
import { DateForm } from './DateForm';
//import { ResevationData } from '../interfaces/ReservationData';
import { StatBooking } from './StatBooking';
import { GuestForm } from './RoomDataForm';
import { room } from '../interfaces/Room';
import { resevationData } from '../interfaces/ReservationData';
export const BookingForm: React.FC = () => {
    const today = new Date().toISOString().split('T')[0];
    const [date, setDate] = useState<date>({ checkIn: today, checkOut: today });
    const [inputClicked, setInputClicked] = useState<boolean>(false);
    const inicialRooms = new Map<number, room>();
    inicialRooms.set(1, { adultCount: 1, child: { childCount: 0, ages: [] } });

    const inicialData: resevationData = {
        date:  { checkIn: today, checkOut: today },
        rooms: inicialRooms,
        guest: { name: '', lastName: '', email: '', phone: '' }
    }
    const [reservationData, setReservationData] = useState<resevationData>()

    const [receivedRooms, setReceivedRooms] = useState<Map<number, room>>(new Map());
    // React utiliza un flujo de datos en una sola dirección, pasando datos hacia abajo de la jerarquía de 
    // componentes desde el componente padre al hijo


    const handleReceivedRooms = (rooms: Map<number, room>) => {
        setReceivedRooms(rooms);
    }

    const handleInputClicked = () => {
        setInputClicked(true);
    }

    console.log(receivedRooms);
    const handleDateChange = (field: keyof date, value: string) => {
        setDate(prevDate => ({
            ...prevDate,
            [field]: value
        }))
    }
    return (
        <div className='BookingForm'>
            <DateForm
                date={date}
                onDateChange={handleDateChange}
            />
            <StatBooking
                rooms={receivedRooms}
                handleInputClicked={handleInputClicked}
            />
            {inputClicked && <div className='GuestForm'>
                <GuestForm
                    onSendRoomData={handleReceivedRooms}
                />
                <button onClick={() => setInputClicked(false)}>Next</button>
            </div>
            }

        </div>
    )
}
