import { on } from 'node:stream';
import React, { useEffect, useState } from 'react'

interface AgesFormProps {
    childCount: number;
    onAgesChange: (newAges: number[]) => void;
}
export const AgesForm: React.FC<AgesFormProps> = ({ childCount, onAgesChange }) => {
    const [ages, setAges] = useState<number[]>(Array(childCount).fill(0));
    useEffect(() => {
        setAges(Array(childCount).fill(0))
    }, [childCount])
    const handleAgesChange = (ageKey: number, newAge: number) => {
        const newAges = [...ages]
        newAges[ageKey] = newAge
        setAges(newAges)
        onAgesChange(newAges)
    }
    return (
        <div className='AgesForm'>
            {ages.map((age, index) => (
                <input
                    key={index}
                    type="number"
                    value={age}
                    onChange={(e) => handleAgesChange(index, Number(e.target.value))}
                />
            ))
            }
        </div>
    )
}
