import React, { useState } from 'react';
import { guest } from '../interfaces/guest';
import "../css/userForm.css"
interface UserFormProps {
  onUserChange: (guest: guest) => void;
  onNext: () => void;
  onPrevious: () => void;
}

export const UserForm: React.FC<UserFormProps> = ({ onUserChange, onNext, onPrevious }) => {
  const [formGuest, setFormGuest] = useState<guest>({
    name: '',
    lastName: '',
    email: '',
    phone: '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onUserChange(formGuest);
    onNext();
  };

  const formUserChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormGuest((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className='UserFormConteiner'
     style={{display:"flex", justifyContent:"center"}}
     >
      <div className="userForm" style={{ display: "flex", justifyContent: 'center' 
        , marginTop:'8rem', padding:"4em",  position:"relative"}}>

        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: "column" }}>
          <div>

            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formGuest.name}
              onChange={formUserChange}
              className='user'
            />
          </div>
          <div>

            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={formGuest.lastName}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formGuest.email}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div>

            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={formGuest.phone}
              onChange={formUserChange}
              required
              className='user'
            />
          </div>
          <div style={{display:"flex", justifyContent:"space-around"}}>
            <button 
                type="submit" 
                style={{padding:"0.3em", marginTop:"2em", border:"none", color:"#fff"
                       , backgroundColor:"#1a1616"}}
              >Next
            </button>
            <button 
                type="button" 
                style={{padding:"0.3em", marginTop:"2em", border:"none"}} 
                onClick={() => onPrevious()}>
                  Previuos
                </button>
          </div>
        </form>
      </div>
    </div>
  );
};
