import React, { useState } from 'react'
import {Date as Idate} from '../interfaces/BookedDates'
import { DateForm } from './DateForm';

export const BookingForm: React.FC = () => {
    const dateToday = new Date().toISOString().split('T')[0]
    const [date, setDate] = useState<Idate>({ checkIn: dateToday, checkOut: dateToday });
    const handleDateChange = (field: keyof Date, value: string) => {
        setDate(prevDate => ({
            ...prevDate,
            date: {
                [field]: value
            }
        }))
    }
    return (
        <div className='BookingForm'>
            <DateForm
                date={date}
                onDateChange={handleDateChange}
            />
        </div>
    )
}
