import React, { useMemo, useState } from 'react'
import { Room } from '../interfaces/Room'
interface StatBookingProps {
    rooms: Room[];
}
export const StatBooking: React.FC<StatBookingProps> = ({ rooms }) => {

    const adultSum = useMemo<number>(() => rooms.reduce((sum, room)  => sum + room.adultCount, 0), [rooms]);
    const childSum = useMemo<number>(() => rooms.reduce((sum, adult) => sum + adult.child.count, 0), [rooms]);
    const roomsSum = useMemo<number>(() => rooms.length, [rooms.length]);

    return (
        <div className='StatBooking'>
            <p>
                {adultSum}, {childSum}, {roomsSum}
            </p>
        </div>
    )
}
