import React, { useMemo, useState} from 'react'
import { Room } from '../interfaces/Room'
import "../css/statBooking.css"
interface StatBookingProps {
    rooms:  Map<number, Room>
    onClicked:(input:boolean) =>void;
}
export const StatBooking: React.FC<StatBookingProps> = ({ rooms, onClicked}) => {
    const [inputClicked, setInputClicked] = useState<boolean>(false);

    const handleOnClicked = (input:boolean) =>{
        setInputClicked(true);
        onClicked(inputClicked);
    }
    const adultSum = useMemo<number>(() => Array.from(rooms.values()).reduce((sum, room)  => sum + room.adultCount, 0),[rooms]);
    const childSum = useMemo<number>(() => Array.from(rooms.values()).reduce((sum, adult) => sum + adult.child.childCount, 0),[rooms]);
    const roomsSum = useMemo<number>(() => rooms.size, [rooms]);

    const adultTitle = adultSum > 1 ?  'adults'  :'adult';
    const childTitle = childSum > 1 ?  'children':'child';
    const roomTitle  = roomsSum > 1 ?  'rooms'   :'room';
    const formattedStat = childSum === 0 ? 
          `${adultTitle} ${adultSum}, ${roomTitle}  ${roomsSum}` : 
          `${adultTitle} ${adultSum}, ${childTitle} ${childSum} ${roomTitle} ${roomsSum}`;

    return (
        <div className='StatBooking'>
            <input
                type="text"
                value={formattedStat}
                readOnly
                onClick={()=>handleOnClicked(inputClicked)}
            />
        </div>
    )
}
