import React from 'react'
import { useBookingContext } from './ContexApplication';
import { Room } from '../interfaces/Room';
import { GuestCart } from './GuestCart';
import { ChildrenAgesComponent } from './ChildrenAgesComponent';
type field = "adultCount" | "childCount";
export const RoomManagemantComponent: React.FC = () => {
    const { bookingData, setBookingData } = useBookingContext();
    const handleAddNewRoom = () => {
        const newRoom: Room = {
            adultCount: 1,
            child: { childCount: 0, ages: [] }
        }
        const updateRooms = new Map(bookingData.rooms);
        updateRooms.set(bookingData.rooms.size + 1, newRoom);
        setBookingData(prev => ({
            ...prev,
            rooms: updateRooms
        }))
    }
    const handleDeleteRoom = (index: number) => {
        setBookingData(prev => {
            const updateRooms = new Map(prev.rooms);
            updateRooms.delete(index);
            return {
                ...prev,
                rooms: updateRooms
            }
        })
    }
    const handleGuestCountChange = (field: field, index: number, value: number) => {
        setBookingData(prev => {
            const updateRooms = new Map(prev.rooms);
            const GuestUpdate = updateRooms.get(index);
            if (GuestUpdate) {
                if (field === 'adultCount') {
                    GuestUpdate.adultCount = value;
                } else if (field === 'childCount') {
                    GuestUpdate.child.childCount = value
                }
                updateRooms.set(index, GuestUpdate);
                return {
                    ...prev,
                    rooms: updateRooms
                }
            }
            return prev;

        });
    }
    const handleAgesChange = (roomIndex: number, key: number, value: number) => {
        setBookingData(prev => {
            const updateRooms = new Map(prev.rooms);
            const updateRoom = updateRooms.get(roomIndex);
            if (updateRoom) {
                const ageUpdate = [...updateRoom.child.ages];
                ageUpdate[key] = value;
                updateRooms.set(roomIndex, { ...updateRoom, child: { ...updateRoom.child, ages: ageUpdate } })
            }
            return { ...prev, rooms: updateRooms }
        })
    }
    return (
        <section
            className='RoomManagemantComponent'
        >
            {Array.from(bookingData.rooms.entries()).map(([roomIndex, room]) => (
                <section key={roomIndex}>
                    <GuestCart
                        onGuestChange={(field, value) => handleGuestCountChange(field, roomIndex, value)}
                        room={room}
                        key={roomIndex}
                    />
                    {roomIndex > 1 && <button onClick={() => handleDeleteRoom(roomIndex)}>delete</button>}
                    {room.child.childCount > 0 &&
                        <ChildrenAgesComponent
                            childrenCount={room.child.childCount}
                            onHandleAgeChange={(key, value) => handleAgesChange(roomIndex, key, value)}
                        />
                    }
                    {room.child.childCount}
                </section>

            ))}
            <button onClick={handleAddNewRoom}>Add New Room</button>
        </section>
    )
}
