import React from 'react';
import { Room } from '../interfaces/Room';
import "../css/guestForm.css";

type field = "adultCount" | "childCount";
interface GuestFormProps {
    rooms: Map<number, Room>;
    onHandleGuestCountChange: (index: number, field: field, value: number) => void;
    onDeleteRoom: (index: number) => void;
    onChildAgesChange: (index: number, ages: number[]) => void;
    onAddRoom: () => void;
    onChildAgeChange: (roomId: number, childId: number, value: number) => void;
    onSetClikedInput: (input: boolean) => void;
}

export const GuestForm: React.FC<GuestFormProps> = (
    {
        rooms,
        onHandleGuestCountChange,
        onDeleteRoom,
        onChildAgesChange,
        onAddRoom,
        onSetClikedInput,
        onChildAgeChange
    }) => {

    return (
        <div className='GuestForm '
            style={{ position: 'absolute' }}
        >
            {Array.from(rooms.entries()).map(([index, room]) => (
                <form key={index} style={{}} >
                    <div style={{ display: "flex" }}>

                        <input
                            type="number"
                            name="adultCount"
                            value={room.adultCount}
                            onChange={(e) => onHandleGuestCountChange(index, 'adultCount', Number(e.target.value))}
                            style={{ marginTop: "0.2em", maxWidth: "10em", textAlign: "center" }}
                        />
                        <input
                            type="number"
                            name="childCount"
                            value={room.child.childCount}
                            onChange={(e) => {
                                const newChildCount = Number(e.target.value);
                                onHandleGuestCountChange(index, 'childCount', newChildCount);
                                if (newChildCount === 0) {
                                    onChildAgesChange(index, []);
                                }
                            }}
                            style={{ marginTop: "0.2em", maxWidth: "10em", textAlign: "center" }}
                        />

                        <div>
                        </div>

                        {index > 1 &&
                            <button type='button'
                                onClick={() => onDeleteRoom(index)}
                                style={
                                    {
                                        padding: "0.5em",
                                        border: "none",
                                        marginLeft: "1rem",
                                        marginTop: "0.2em"
                                    }
                                }
                            >
                                Delete
                            </button>
                        }
                    </div>
                    {room.child.childCount > 0 && (
                        <div className='ChildForm'
                            style={
                                {
                                    backgroundColor: "#1a2a3a",
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "center",
                                    justifyContent: "center",
                                    paddingTop: "1em",
                                    paddingBottom: "1em"
                                }
                            }
                        >
                            {Array.from({ length: room.child.childCount }).map((_, key) => (

                                <select name="" id="" key={key}
                                    value={room.child.ages[key] !== undefined ? room.child.ages[key].toString() : ""}
                                    onChange={(e) => onChildAgeChange(index, key, Number(e.target.value))}
                                >
                                    <option value="">child {key + 1} Select Age</option>
                                    <option value={1}>1</option>
                                    <option value={2}>2</option>
                                    <option value={3}>3</option>
                                    <option value={4}>4</option>
                                    <option value={5}>5</option>
                                    <option value={6}>6</option>
                                    <option value={7}>7</option>
                                    <option value={8}>8</option>
                                    <option value={9}>9</option>
                                    <option value={10}>10</option>
                                    <option value={11}>11</option>
                                    <option value={12}>12</option>
                                    <option value={13}>13</option>
                                    <option value={14}>14</option>
                                </select>

                            ))
                            }
                        </div>
                    )
                    }
                </form>

            ))}
            <div style={{ marginTop: "1rem", display: "flex", gap: "1em" }}>

                <button
                    type='button' onClick={onAddRoom}
                    style={{ padding: "0.5em", border: 'none' }}
                >Add Room
                </button>
                <button
                    type='button'
                    onClick={(inputClicked) => onSetClikedInput(!inputClicked)}
                    style={{ padding: "0.5em", border: "none" }}
                >
                    Next
                </button>
            </div>
        </div>
    );
};
