import React, { useEffect, useState } from 'react';
import { Room } from '../interfaces/Room';
import { AgesForm } from './AgesForm';

type Field = 'adultCount' | 'childCount';

interface GuestFormProps {
    onSendRoomData: (rooms: Room[]) => void;
}

export const GuestForm: React.FC<GuestFormProps> = ({ onSendRoomData }) => {
    const initialRoom: Room = { adultCount: 1, child: { childCount: 0, ages: [] } };
    const [roomsData, setRoomsData] = useState<Room[]>([initialRoom]);

    const handleAddRoom = (childCount: number) => {
        const newRoom: Room = {
            adultCount: 1,
            // Solo incluir el objeto 'child' si childCount es mayor que cero
            ...(childCount > 0 && {
                child: {
                    childCount: childCount,
                    ages: []
                }
            })
        };

        setRoomsData(prevRoomsData => [...prevRoomsData, newRoom]);
    };

    useEffect(() => {
        onSendRoomData(roomsData);
    }, [roomsData, onSendRoomData]);

    console.log(roomsData, 'From Rooms');

    const handleAgesChange = (updateRoomIndex: number, newAges: number[]) => {
        setRoomsData(prevRoomsData => {
            return prevRoomsData.map((room, roomIndex) =>
                roomIndex === updateRoomIndex ? {
                    ...room,
                    child: {
                        ...room.child!,
                        ages: newAges
                    }
                } : room
            );
        });
    };

    const handleDeleteRoom = (roomIndex: number) => {
        setRoomsData(prevRooms => {
            return prevRooms.filter((_, index) => index !== roomIndex);
        });
    };

    interface Child {
        childCount: number;
        ages: number[];
    }

    interface Room {
        adultCount: number;
        child?: Child;
    }

    type Field = 'adultCount' | 'childCount';

    const handleRoomGuestChange = (roomIndex: number, field: Field, value: number) => {
        setRoomsData(prevRooms => {
            const newRooms = [...prevRooms];
            const roomToUpdate = { ...newRooms[roomIndex] };

            if (field === 'adultCount') {
                roomToUpdate.adultCount = value;
            } else if (field === 'childCount') {
                roomToUpdate.child = {
                    ...roomToUpdate.child,
                    childCount: value,
                    ages: roomToUpdate.child?.ages || [] 
                };
            }

            newRooms[roomIndex] = roomToUpdate;
            return newRooms;
        });
    };

    return (
        <div className='GuestForm'>
            {roomsData.map((room, roomIndex) => (
                <form key={roomIndex}>
                    <input
                        type="number"
                        value={room.adultCount}
                        onChange={(e) => handleRoomGuestChange(roomIndex, 'adultCount', Number(e.target.value))}
                    />
                    {room.child && (
                        <>
                            <input
                                type="number"
                                value={room.child.childCount}
                                onChange={(e) => handleRoomGuestChange(roomIndex, 'childCount', Number(e.target.value))}
                            />
                            {room.child.childCount > 0 && (
                                <AgesForm
                                    childCount={room.child.childCount}
                                    onChildrenAges={(newChildAges) => handleAgesChange(roomIndex, newChildAges)}
                                />
                            )}
                        </>
                    )}

                    {roomIndex > 0 && (
                        <button type='button' onClick={() => handleDeleteRoom(roomIndex)}>Delete</button>
                    )}
                </form>
            ))}
            <button onClick={()=>handleAddRoom}>addRoom</button>
        </div>
    );
};
