import React from 'react';
import { Room } from '../interfaces/Room';
import { useBookingContext } from './ContexApplication';
import "../css/handleguest.css";
interface HandleGuestProps {
    room: Room;
    roomIndex: number;
}
export const HandleGuest: React.FC<HandleGuestProps> = ({ room, roomIndex }) => {
    const { adultQuantityDecrement,
        adultQuantityIncrement,
        childrenQuantityDecrement,
        childrenQuantityIncrement,
        handleDeleteRoom
    }
        = useBookingContext();
    return (
        <section className='HandleGuest' >
            <p style={{ fontWeight: "bold", marginBottom: "1rem" }}>Room  {roomIndex}</p>
            <div className='flex'>
                <div style={{display:"flex", gap:"2rem", alignItems:"center"}} className='Adult__ButtonsConteiner'>
                    <button
                        onClick={() => adultQuantityIncrement(roomIndex)}
                    >+
                    </button>
                    <p>{room.adultCount}</p>
                    <button
                        onClick={() => adultQuantityDecrement(roomIndex)}
                    >-
                    </button>
                </div>
                <div style={{display:"flex", gap:"2rem", alignItems:"center"}} className='Children__ButtonsConteiner'>
                    <button
                        onClick={() => childrenQuantityIncrement(roomIndex)}
                    >+
                    </button>
                    <p>{room.child.childCount}</p>
                    <button
                        onClick={() => childrenQuantityDecrement(roomIndex)}
                    >-
                    </button>
                </div>
                <div>

                {roomIndex > 1 &&

                    <button
                        onClick={() => handleDeleteRoom(roomIndex)}

                    > delete
                    </button>
                }
                </div>
            </div>

        </section>
    )
}
